var chai = require('chai');
var assert = chai.assert;
var valid = require('../../lib/utils/inputValidator');


describe('valid.isArrayOfAttachedTrytes', function() {

    var tests = [
        [
            '',
            ''
        ],
        [
            ''
        ],
        'ABCDWDUOSTSJVL9EEHKW9YQFPBVBJAGLNKRVGSQZCGHQWEMIIILJMTHVAGVDXJVZMBAMOZTSBQRVNLLSJ',
        {
            address: 'JALLWDTSJVL9EEHKW9YQFPBVBJAGLNKRVGSQZCGHQWEMIIILJMTHVAGVDXJVZMBAMOZTSBQNRVNLLSJMP'
        }
    ]

    // 0 test
    it('should be valid isArrayOfAttachedTrytes: 0', function() {

        var isArrayOfAttachedTrytes = valid.isArrayOfAttachedTrytes(tests[0]);
        assert.isTrue(isArrayOfAttachedTrytes);
    })

    // 1 test
    it('should be invalid isArrayOfAttachedTrytes: 1', function() {

        var isArrayOfAttachedTrytes = valid.isArrayOfAttachedTrytes(tests[1]);
        assert.isFalse(isArrayOfAttachedTrytes);
    })

    // 2 test
    it('should be invalid isArrayOfAttachedTrytes: 2', function() {

        var isArrayOfAttachedTrytes = valid.isArrayOfAttachedTrytes(tests[2]);
        assert.isFalse(isArrayOfAttachedTrytes);
    })

    // 3 test
    it('should be invalid isArrayOfAttachedTrytes: 3', function() {

        var isArrayOfAttachedTrytes = valid.isArrayOfAttachedTrytes(tests[3]);
        assert.isFalse(isArrayOfAttachedTrytes);
    })

});
