var chai = require('chai');
var assert = chai.assert;
var valid = require('../../lib/utils/inputValidator');


describe('valid.isArrayOfTrytes', function() {

    var tests = [
        [
            '',
            ''
        ],
        [
            'JALLWDUOSTSJVL9EEHKW9YQFPBVBJAGLNKRVGSQZCGHQWEMIIILJMTHVAGVDXJVZMBAMOZTSBQNRVNLLSJMPIVGPNE',
            'fdsafBCDWDUOSTSJEEHKW9YQFPBVBJAGLNKRVGSQZCGHQWEMIIILJMTHVAGVDXJVZMBAMOZTSBQRVNLLSJ'
        ],
        'ABCDWDUOSTSJVL9EEHKW9YQFPBVBJAGLNKRVGSQZCGHQWEMIIILJMTHVAGVDXJVZMBAMOZTSBQRVNLLSJ',
        {
            address: 'JALLWDTSJVL9EEHKW9YQFPBVBJAGLNKRVGSQZCGHQWEMIIILJMTHVAGVDXJVZMBAMOZTSBQNRVNLLSJMP'
        }
    ]

    // 0 test
    it('should be valid isArrayOfTrytes: 0', function() {

        var isArrayOfTrytes = valid.isArrayOfTrytes(tests[0]);
        assert.isTrue(isArrayOfTrytes);
    })

    // 1 test
    it('should be invalid isArrayOfTrytes: 1', function() {

        var isArrayOfTrytes = valid.isArrayOfTrytes(tests[1]);
        assert.isFalse(isArrayOfTrytes);
    })

    // 2 test
    it('should be invalid isArrayOfTrytes: 2', function() {

        var isArrayOfTrytes = valid.isArrayOfTrytes(tests[2]);
        assert.isFalse(isArrayOfTrytes);
    })

    // 3 test
    it('should be invalid isArrayOfTrytes: 3', function() {

        var isArrayOfTrytes = valid.isArrayOfTrytes(tests[3]);
        assert.isFalse(isArrayOfTrytes);
    })

});
